                    if (!(simulation.cycle % Math.floor(100 + 90 * Math.random() * simulation.CDScale))) {
                        this.diveAttack()
                    }
                    ctx.beginPath();
                    ctx.arc(this.position.x, this.position.y, 1000, 0, 2 * Math.PI);
                    ctx.fillStyle = "#f5f5ff";
